mapper.select:
SELECT service
FROM TB_PRODUCT
WHERE vendor = #{vendor}

mapper.insert:
INSERT INTO TB_PRODUCT (
    vendor
,   service
,   productName
,   productType
,   category
,   rate
,   minOrder
,   maxOrder
,   refill
,   dripfeed
,   cancel
) VALUES
(
    #{vendor}
,   #{service}
,   #{name}
,   #{type}
,   #{category}
,   #{rate}
,   #{min}
,   #{max}
,   #{refill}
,   #{dripfeed}
,   #{cancel}
)

mapper.update:
UPDATE TB_PRODUCT
SET  productName = #{name}
,   productType = #{type}
,   category = #{category}
,   rate = #{rate}
,   minOrder = #{min}
,   maxOrder = #{max}
,   refill = #{refill}
,   dripfeed = #{dripfeed}
,   cancel = #{cancel}
WHERE vendor = #{vendor}
AND service = #{service}
