mapper.select:
SELECT * FROM TB_ORDER WHERE vendor = #{vendor}
AND ( orderId LIKE CONCAT( '%', #{keyword}, '%' ) OR service LIKE CONCAT( '%', #{keyword}, '%' ) OR link LIKE CONCAT( '%', #{keyword}, '%' ) OR quantity LIKE CONCAT( '%', #{keyword}, '%' ) OR runs LIKE CONCAT( '%', #{keyword}, '%' ) OR orderInterval LIKE CONCAT( '%', #{keyword}, '%' ) OR regDate LIKE CONCAT( '%', #{keyword}, '%' ) )
ORDER BY #{sortColumn} #{sortType}
LIMIT ${offset}, ${recodeSize}

mapper.insert:
INSERT INTO TB_ORDER (
    orderId
,   vendor
,   orderAction
,   service
,   link
,   quantity
,   runs
,   orderInterval
,   regDate
) VALUES
(
    #{orderId}
,   #{vendor}
,   #{action}
,   #{service}
,   #{link}
,   #{quantity}
,   #{runs}
,   #{interval}
,   NOW()
)
